import React from "react";
import {v1 as uuid} from "uuid";
import axios from "axios";
import PlayingCard from "./PlayingCard";
import "./PlayingCardList.css";
import useAxios from "./hooks";


/* Renders a list of playing cards.
 * Can also add a new card at random. */
function CardTable() {
  const [cards, addCard] = useAxios("https://deckofcardsapi.com/api/deck/new/draw/");
  const addNewCard = async () => {
    let cardData = await axios.get("https://deckofcardsapi.com/api/deck/new/draw/");
    addCard(cardData.data.cards[0]);
  };
  return (
    <div className="PlayingCardList">
      <button onClick={addNewCard}>Add a playing card!</button>
      <div className="PlayingCardList-card-area">
        {cards.map(cardData => (
          <PlayingCard key={uuid()} front={cardData.image} />
        ))}
      </div>
    </div>
  );
}

CardTable.defaultProps = {};

export default CardTable;
